
export interface ISeparators {
    thousand: string;
    decimal: string;
}
export function numberFormat(value: number|string, format: string, context?: boolean, separators?: ISeparators): string;

export function stripColors(format: string): string;

export interface INumberObject {
    label: string;
    color: string;
    backgroundColor: string;
}
export function colors2Object(formattedNumber: string) : INumberObject;

export function containsNullConditionalFormat(format: string): boolean;

export namespace FormatTranslator {
    export function translate2default(formatString: string, separators: ISeparators): string;

    export function translate2custom(formatString: string, separators: ISeparators): string;
}
